INSERT INTO `article` VALUES ('13', '页面可用图标', '# 打算打打算打\r\n\r\n&lt;style type=&quot;text/css&quot;&gt;\r\n[class^=&quot;icon-&quot;] {\r\n	position: relative;\r\n	z-index: 10;\r\n	margin: 10px 5px;\r\n	font-size: 36px;\r\n	-webkit-transition: -webkit-transform .3s ease-out, color .3s ease-out;\r\n	transition: transform .3s ease-out, color .3s ease-out;\r\n}\r\n[class^=&quot;icon-&quot;]:hover {\r\n	-webkit-transform: scale(2);\r\n	transform: scale(2);\r\n	color: #de0b1b;\r\n}\r\n&lt;/style&gt;\r\n\r\n&lt;span class=&quot;icon-adjust4&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-align14&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-align15&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-align16&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-ambulance8&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-anchor16&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-android4&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-angle&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-angle1&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-angle2&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-apple22&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-archive15&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-arrow459&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-arrow460&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-arrow461&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-arrow462&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-arrow463&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-arrow464&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-arrow465&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-arrow466&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-arrow467&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-arrowhead5&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-arrowhead6&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-arrowheads&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-asterisk2&quot;&gt;&lt;/span&gt;&lt;span class=&quot;icon-ban&quot;&gt;&lt;/span&gt;&lt;span class=&quo